// PopupContentWithInput.js

import React, { useState } from 'react';
import "../styles/style.scss";

const PopupContentWithInput = ({ title, onSubmit, onClose, inputCount }) => {
  const initialInputValues = Array.from({ length: inputCount }, () => '');
  const [inputValues, setInputValues] = useState(initialInputValues);

  const handleInputChange = (index, value) => {
    const newInputValues = [...inputValues];
    newInputValues[index] = value;
    setInputValues(newInputValues);
  };

  const handleSubmit = () => {
    onSubmit(inputValues);
    onClose(); // 關閉彈窗
  };

  return (
    <div className="popup-overlay">
      <div className="popup">
        <div className="popup-content">
          <span className="close" onClick={onClose}>&times;</span>
          <p className='font24'>{title}</p>
          {inputValues.map((value, index) => (
            <input
              key={index}
              type="text"
              value={value}
              onChange={(e) => handleInputChange(index, e.target.value)}
            />
          ))}
          <button onClick={handleSubmit}>確定</button>
        </div>
      </div>
    </div>
  );
};

export default PopupContentWithInput;
