import React from "react";
import "../styles/style.scss";
import Sidebar from './Sidebar';
import DataList from './DataList';
// import { usePopup } from './PopupContext';
import PopupContentWithInput from './PopupContentWithInput';



const Teacher = () => {

    // const { teacherPopup, openTeacherPopup, closeTeacherPopup } = usePopup();

    const { teacherPopup, openTeacherPopup, closeTeacherPopup } = usePopup();

    const handleButtonClick = () => {
      openTeacherPopup(<PopupContentWithInput onSubmit={handleTeacherSubmit} onClose={closeTeacherPopup} />);
    };

    const handleTeacherSubmit = (inputValue) => {
      console.log('Teacher 彈窗確定按鈕被點擊，輸入的值是:', inputValue);
      // 在這裡處理 Teacher 彈窗中確定按鈕被點擊時的邏輯
    };

    return(
    <div className="main">
        <Sidebar />
        <div className="main_container">
            <div className="data_header">
                <p className="font32">教師管理</p>
                <button onClick={handleButtonClick}>新增教師</button>
                {teacherPopup.isOpen && (
        <div className="popup">
          <div className="popup-content">
            <span className="close" onClick={closeTeacherPopup}>&times;</span>
            {teacherPopup.content}
          </div>
        </div>
      )}

            </div>
            <DataList />
            <div className="list_teacher">

            </div>
        </div>
    </div>
    )
}

export default Teacher;
